import axios, {AxiosResponse} from 'axios'

const baseUrl: string = 'http://localhost:5000/api/v1'

export const loginUser = async (email:string, password:string): Promise<AxiosResponse<UserApiDataType>> => {
    try{

        const user: AxiosResponse<UserApiDataType> = await axios.get(baseUrl+'/users', {data: {email:email, password: password}}).then(res =>{return user})
        return user

    }catch(error: any){
        throw new Error(error)
    }
}
